import React from 'react';
import '../styles/Footer.css';
import WhatsAppLogo from '../assets/whatsapp-logo.png';
import GmailLogo from '../assets/gmail-logo.png';
import InstagramLogo from '../assets/instagram-logo.png';
import YouTubeLogo from '../assets/youtube-logo.png';
import FacebookLogo from '../assets/facebook-logo.png';
import TwitterLogo from '../assets/twitter-logo.png';
import ThreadsLogo from '../assets/threads-logo.png';

const Footer = () => {
    const ContactSection = () => {
    const handleWhatsAppClick = () => {
        window.open('https://wa.me/917866049865', '_blank');
    };

    const handleEmailClick = () => {
      // Open Gmail with a pre-filled "To" field
    window.open('https://mail.google.com/mail/?view=cm&fs=1&to=asversity.helpdesk@gmail.com', '_blank');
};

    return (
        <div className="contact-container">
            <div className="contact-section">
                <h2>Contact Us</h2>
                <p>If you have any questions, feel free to reach out to us!</p>
                
                <div className="button-container">
                    <button onClick={handleWhatsAppClick} className="contact-whatsapp-button">
                        <img src={WhatsAppLogo} alt="WhatsApp Logo" className="button-icon" />
                        Send Message
                    </button>
                    
                    <button onClick={handleEmailClick} className="contact-email-button">
                        <img src={GmailLogo} alt="Gmail Logo" className="button-icon" />
                        Send Email
                    </button>
                </div>
            </div>

            <div className="follow-us-section">
                <h3 className="follow-us-heading">Follow Us</h3>
                <div className="social-icons">
                    <a href="https://www.instagram.com/asversity.insta/profilecard/?igsh=bXNiNGFjcXFwcnVq" target="_blank" rel="noopener noreferrer" className="social-icon">
                        <img src={InstagramLogo} alt="Instagram" />
                    </a>
                    <a href="https://youtube.com/@asversity_technologies?si=cGzrBWY0-kSOzmdP" target="_blank" rel="noopener noreferrer" className="social-icon">
                        <img src={YouTubeLogo} alt="YouTube" />
                    </a>
                    <a href="https://www.facebook.com/asversityfacebook?mibextid=ZbWKwL" target="_blank" rel="noopener noreferrer" className="social-icon">
                        <img src={FacebookLogo} alt="Facebook" />
                    </a>
                    <a href="https://x.com/Tcepsus_Vom?t=wbzoSwBzlryCJUYXVq9Eaw&s=09" target="_blank" rel="noopener noreferrer" className="social-icon">
                        <img src={TwitterLogo} alt="Twitter" />
                    </a>
                    <a href="https://www.threads.net/@asversity.insta?invite=0" target="_blank" rel="noopener noreferrer" className="social-icon">
                        <img src={ThreadsLogo} alt="Threads" />
                    </a>
                </div>
            </div>
        </div>
    );
};
    const openNewWindow = (title, content) => {
        const newWindow = window.open(
            '',
            '_blank',
            'width=600,height=600'
        );

        newWindow.document.write(`
            <!DOCTYPE html>
            <html lang="en">
            <head>
                <meta charset="UTF-8">
                <meta name="viewport" content="width=device-width, initial-scale=1.0">
                <title>${title}</title>
                <style>
                    body { font-family: Arial, sans-serif; padding: 20px; color: #333; line-height: 1.6; }
                    h1 { font-size: 24px; color: #4557f9; }
                    p, li { margin-bottom: 10px; }
                    ul { padding-left: 20px; }
                </style>
            </head>
            <body>
                <h1>${title}</h1>
                <div>${content}</div>
            </body>
            </html>
        `);

        newWindow.document.close();
    };

    const privacyPolicyContent = `
        <p><strong>Effective Date:</strong> [20.09.2024]</p>
        <p><strong>1. Introduction:</strong> AS Versity Technologies is committed to protecting your privacy in compliance with the Information Technology Act, 2000 and the IT Rules, 2011. This Privacy Policy outlines our practices regarding the collection, use, and disclosure of your personal information.</p>
        <p><strong>2. Information We Collect:</strong> We may collect personal data like your name, email, etc., and sensitive information such as passwords only with your consent.</p>
        <p><strong>3. Purpose of Collecting Information:</strong> To provide services, communicate with you, and ensure compliance with legal obligations.</p>
        <p><strong>4. Disclosure of Information:</strong> We do not sell or lease your data. Information may be shared with trusted partners only as needed for operations and with consent.</p>
        <p><strong>5. Reasonable Security Practices:</strong> We follow IT Rules, 2011, by implementing secure practices like encryption.</p>
        <p><strong>6. Data Retention:</strong> Data is retained only as long as necessary.</p>
        <p><strong>7. Your Rights:</strong> You can access, correct, and withdraw consent for your data.</p>
        <p><strong>8. Cookies:</strong> We use cookies to improve user experience. You can disable them, but functionality may be affected.</p>
        <p><strong>9. Changes to This Policy:</strong> We may update it as needed.</p>
        <p><strong>10. Grievance Redressal:</strong> Contact our Grievance Officer at [asversity.helpdesk@gmail.com] for privacy-related issues.</p>
    `;

    const termsContent = `
        <p><strong>Effective Date:</strong> [20.09.2024]</p>
        <p><strong>1. Introduction:</strong> These Terms and Conditions govern the use of AS Versity Technologies in line with the IT Act, 2000.</p>
        <p><strong>2. Eligibility:</strong> Our services are for users in India aged 18 and over, or minors with guardian consent.</p>
        <p><strong>3. Service Usage:</strong> You agree to lawful use of our services.</p>
        <p><strong>4. Account Security:</strong> You are responsible for safeguarding your account details.</p>
        <p><strong>5. Intellectual Property Rights:</strong> All content is the property of AS Versity Technologies, protected under Indian laws.</p>
        <p><strong>6. Limitation of Liability:</strong> We strive for accuracy but are not liable for damages from service use.</p>
        <p><strong>7. User Content:</strong> By posting content, you grant us rights to use it for our services.</p>
        <p><strong>8. Modifications and Termination:</strong> We may update terms and terminate services at our discretion.</p>
        <p><strong>9. Governing Law and Jurisdiction:</strong> These Terms are governed by Indian laws, with disputes resolved in [INDIA].</p>
        <p><strong>10. Compliance:</strong> In compliance with Section 43A of the IT Act, we use secure practices to protect data.</p>
        <p><strong>11. Amendments:</strong> AS Versity Technologies reserves the right to amend these terms.</p>
    `;

    return (
        <footer className="footer">
            <p>© 2024 AS Versity Technologies. All rights reserved.</p>
            <p>
                <span
                    className="clickable-text"
                    onClick={() => openNewWindow('Privacy Policy', privacyPolicyContent)}
                >
                    Privacy Policy
                </span>
                {' | '}
                <span
                    className="clickable-text"
                    onClick={() => openNewWindow('Terms and Conditions', termsContent)}
                >
                    Terms of Service
                </span>
            </p>
        </footer>
    );
};

export default Footer;
