// src/components/Header.js
import { useState } from 'react';
import '../styles/Header.css';
import { auth, provider, signInWithPopup, signOut } from '../firebase';
import logo from '../assets/logo.png';

function Header() {
  const [user, setUser] = useState(null);

  const handleLogin = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
    } catch (error) {
      console.error("Login failed:", error);
      alert("Login failed. Try again.");
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      setUser(null);
    } catch (error) {
      console.error("Logout failed:", error);
    }
  };

  return (
    <header className="header">
      <div className="company-info">
        <img src={logo} alt="Company Logo" className="company-logo" />
        <div>
          <div className="CompanyName">ASCORP</div>
          <div className="CompanySlogan">Beyond Possibilities</div>
        </div>
      </div>

      <nav className="nav-buttons">
        {user ? (
          <button className="nav-button login-button" onClick={handleLogout}>
            Logout ({user.displayName})
          </button>
        ) : (
          <button className="nav-button login-button" onClick={handleLogin}>
            Login
          </button>
        )}
      </nav>
    </header>
  );
}

export default Header;
