import { useState } from "react";

const membersData = [
  {
    id: 1,
    name: "Dr. Aryan Vortex",
    email: "aryan@domain.com",
    experience: "15+ Years in Quantum Computing",
    bio: "Dr. Aryan is a pioneer in the field of time-loop algorithms, known for blending AI with interdimensional physics. He serves on multiple interstellar advisory boards.",
    img: "https://via.placeholder.com/300x300",
  },
  {
    id: 2,
    name: "Prof. Luna Cipher",
    email: "luna@domain.com",
    experience: "10+ Years in Astro-Cybernetics",
    bio: "Luna has led multiple missions involving AI-assisted exploration of wormholes. Her bio-synthetic interface theories are groundbreaking.",
    img: "https://via.placeholder.com/300x300",
  },
  // …add more members here…
];

export default function BoardMembers() {
  // track open/closed bios keyed by member id
  const [openIds, setOpenIds] = useState([]);

  const toggle = (id) => {
    setOpenIds((prev) =>
      prev.includes(id) ? prev.filter((x) => x !== id) : [...prev, id]
    );
  };

  return (
    <div className="bg-black text-white min-h-screen p-10 sci-fi-bg">
      <h1 className="text-4xl md:text-5xl font-bold text-center neon-text mb-12">
        Board of Directors
      </h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-10">
        {membersData.map((m) => {
          const isOpen = openIds.includes(m.id);
          return (
            <div
              key={m.id}
              className="glass neon-border rounded-2xl p-6 transition-transform hover:scale-105 duration-300 shadow-lg"
            >
              <img
                src={m.img}
                alt={m.name}
                className="rounded-xl w-full mb-4 border-2 border-cyan-400"
              />

              <h2 className="text-2xl font-semibold neon-text">{m.name}</h2>
              <p className="text-cyan-300">
                Email:{" "}
                <a href={`mailto:${m.email}`} className="underline">
                  {m.email}
                </a>
              </p>
              <p className="text-gray-300">Experience: {m.experience}</p>

              {isOpen && (
                <div className="mt-4 text-gray-200">
                  <p>{m.bio}</p>
                </div>
              )}

              <button
                onClick={() => toggle(m.id)}
                className="mt-4 bg-cyan-500 hover:bg-cyan-400 text-black font-bold py-2 px-4 rounded transition duration-300"
              >
                {isOpen ? "Hide Bio" : "Show Bio"}
              </button>
            </div>
          );
        })}
      </div>
    </div>
  );
}
