// src/components/AvailableCourses.js
import { useState, memo } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import '../styles/AvailableCourses.css';

import FinanceQR  from '../assets/financeQR.png';
import PromptQR   from '../assets/promptQR.png';
import FinanceImg from '../assets/mf.jpg';
import PromptImg  from '../assets/prompt.jpg';

/* ─── Static Course Data ───────────────────────── */
const courseData = [
  {
    id: 1,
    name: 'Finance',
    image: FinanceImg,
    description: [
      'Note: What We Will Cover! (From Scratch)',
      '1. Stock Market',
      '2. Mutual Fund',
      '3. Investment in Gold',
      '4. Lending Money',
      '5. Smart FD',
      '6. Portfolio Management',
      '7. AI Systems in Finance',
    ],
    duration: '4 Month (16 live Classes, 2 hours each)',
    price: '1999 INR',
    qr: FinanceQR,
  },
  {
    id: 2,
    name: 'Prompt Engineering',
    image: PromptImg,
    description: [
      'What We Will Cover!:',
      '1. AI-Powered Image Generation.',
      '2. Text-to-Image and Image-to-Video Creation.',
      '3. Custom Banners, Thumbnails, and Graphic Design.',
      '4. Logo and Branding Design.',
      '5. Content Generation for Blogs and Marketing.',
      '6. Real-Time Language Translation and Summarization.',
      '7. Grammar and Style Enhancement for Writing.',
      '8. Email Drafting and Reply Suggestions.',
      '9. Website Development Using AI',
      '10. Resume Building Using AI.',
    ],
    duration: '2 Month (8 live Class, 2 hours each)',
    price: '1199 INR',
    qr: PromptQR,
  },
];

/* ─── Motion Variants ─────────────────────────── */
const listVariants   = { hidden: { opacity: 0 }, show: { opacity: 1, transition: { staggerChildren: .1 } } };
const itemVariants   = { hidden: { opacity: 0, y: 20 }, show: { opacity: 1, y: 0 } };
const cardVariants   = { exit: { opacity: 0, y: 30 }, enter: { opacity: 1, y: 0 } };
const qrVariants     = { hidden: { scale: 0 }, show: { scale: 1 } };

/* ─── Pure memo’d Thumbnail Item ───────────────── */
const CourseThumb = memo(({ course, onClick }) => (
  <motion.li
    className="course-item"
    variants={itemVariants}
    whileHover={{ scale: 1.05 }}
    whileTap={{ scale: 0.97 }}
    onClick={() => onClick(course)}
  >
    <img src={course.image} alt={`${course.name} thumbnail`} className="course-thumbnail" />
    <span>{course.name}</span>
  </motion.li>
));

/* ─── Component ───────────────────────────────── */
const AvailableCourses = () => {
  const [selected, setSelected] = useState(null);
  const [showQR,   setShowQR]   = useState(false);

  return (
    <div id="available-courses" className="courses-container">
      <h2>Available Courses</h2>

      <AnimatePresence mode="wait">
        {/* ── Course List ───────────────────── */}
        {!selected && (
          <motion.ul
            key="list"
            className="course-list"
            variants={listVariants}
            initial="hidden"
            animate="show"
            exit="hidden"
          >
            {courseData.map(c => (
              <CourseThumb key={c.id} course={c} onClick={setSelected} />
            ))}
          </motion.ul>
        )}

        {/* ── Detail Card ──────────────────── */}
        {selected && (
          <motion.div
            key="details"
            className="course-details"
            variants={cardVariants}
            initial="exit"
            animate="enter"
            exit="exit"
            transition={{ type: 'spring', stiffness: 60, damping: 15 }}
          >
            <button className="back-button" onClick={() => showQR ? setShowQR(false) : setSelected(null)}>
              ← Back
            </button>

            <h3>{selected.name}</h3>
            {selected.description.map((d, i) => (
              <p key={i}>{d}</p>
            ))}
            <p>Duration: {selected.duration}</p>

            <div className="price-and-pay">
              <button className="price">Price: {selected.price}</button>
              <motion.button
                className="pay-button"
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => setShowQR(!showQR)}
              >
                {showQR ? 'Close' : 'Pay'}
              </motion.button>
            </div>

            {/* QR reveal */}
            <AnimatePresence>
              {showQR && (
                <motion.div
                  className="qr-code"
                  variants={qrVariants}
                  initial="hidden"
                  animate="show"
                  exit="hidden"
                  transition={{ type: 'spring', stiffness: 120 }}
                >
                  <p>Scan to pay via Google Pay</p>
                  <img src={selected.qr} alt={`${selected.name} QR`} />
                </motion.div>
              )}
            </AnimatePresence>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default AvailableCourses;
