import { useState, useEffect } from 'react';
import '../styles/Header.css';
import { auth, provider } from '../firebase';
import { signInWithPopup, signOut, onAuthStateChanged } from 'firebase/auth';
import logo from '../assets/logo.png';

function Header() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
    });

    return () => unsubscribe();
  }, []);

  const handleLogin = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      const user = result.user;
      setUser(user);

      // Optional: Display welcome message
      alert(`Welcome, ${user.displayName}!`);
    } catch (error) {
      console.error('Sign-in error:', error.message);
      alert(`Sign-in failed: ${error.message}`);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      setUser(null);
    } catch (error) {
      console.error('Logout error:', error.message);
    }
  };

  return (
    <header className="header">
      <div className="company-info">
        <img src={logo} alt="Company Logo" className="company-logo" />
        <div>
          <div className="CompanyName">ASCORP</div>
          <div className="CompanySlogan">Beyond Possibilities</div>
        </div>
      </div>

      <nav className="nav-buttons">
        {user ? (
          <button className="nav-button login-button" onClick={handleLogout}>
            Logout ({user.displayName})
          </button>
        ) : (
          <button className="nav-button login-button" onClick={handleLogin}>
            Login
          </button>
        )}
      </nav>
    </header>
  );
}

export default Header;
