// src/components/AvailableCourses.js
import { useState, memo } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

import FinanceQR from '../assets/financeQR.png';
import PromptQR from '../assets/promptQR.png';
import FinanceImg from '../assets/mf.jpg';
import PromptImg from '../assets/prompt.jpg';

const courseData = [
  {
    id: 1,
    name: 'Finance',
    image: FinanceImg,
    description: [
      'Note: What We Will Cover! (From Scratch)',
      '1. Stock Market',
      '2. Mutual Fund',
      '3. Investment in Gold',
      '4. Lending Money',
      '5. Smart FD',
      '6. Portfolio Management',
      '7. AI Systems in Finance',
    ],
    duration: '4 Month (16 live Classes, 2 hours each)',
    price: '1999 INR',
    qr: FinanceQR,
  },
  {
    id: 2,
    name: 'Prompt Engineering',
    image: PromptImg,
    description: [
      'What We Will Cover!:',
      '1. AI-Powered Image Generation.',
      '2. Text-to-Image and Image-to-Video Creation.',
      '3. Custom Banners, Thumbnails, and Graphic Design.',
      '4. Logo and Branding Design.',
      '5. Content Generation for Blogs and Marketing.',
      '6. Real-Time Language Translation and Summarization.',
      '7. Grammar and Style Enhancement for Writing.',
      '8. Email Drafting and Reply Suggestions.',
      '9. Website Development Using AI',
      '10. Resume Building Using AI.',
    ],
    duration: '2 Month (8 live Class, 2 hours each)',
    price: '1199 INR',
    qr: PromptQR,
  },
];

const listVariants = {
  hidden: { opacity: 0 },
  show: { opacity: 1, transition: { staggerChildren: 0.1 } },
};
const itemVariants = {
  hidden: { opacity: 0, y: 20 },
  show: { opacity: 1, y: 0 },
};
const cardVariants = {
  exit: { opacity: 0, y: 30 },
  enter: { opacity: 1, y: 0 },
};
const qrVariants = {
  hidden: { scale: 0 },
  show: { scale: 1 },
};

const CourseThumb = memo(({ course, onClick }) => (
  <motion.li
    className="bg-zinc-800 rounded-xl shadow-md overflow-hidden cursor-pointer hover:scale-105 transition-transform duration-200"
    variants={itemVariants}
    onClick={() => onClick(course)}
  >
    <img
      src={course.image}
      alt={`${course.name} thumbnail`}
      className="w-full h-40 object-cover"
    />
    <span className="block text-center text-white font-semibold py-3">
      {course.name}
    </span>
  </motion.li>
));

const AvailableCourses = () => {
  const [selected, setSelected] = useState(null);
  const [showQR, setShowQR] = useState(false);

  return (
    <div id="available-courses" className="max-w-6xl mx-auto px-4 py-10 text-white font-sans">
      <h2 className="text-3xl font-bold text-center text-indigo-400 mb-10">Available Courses</h2>

      <AnimatePresence mode="wait">
        {!selected && (
          <motion.ul
            key="list"
            className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-6"
            variants={listVariants}
            initial="hidden"
            animate="show"
            exit="hidden"
          >
            {courseData.map((c) => (
              <CourseThumb key={c.id} course={c} onClick={setSelected} />
            ))}
          </motion.ul>
        )}

        {selected && (
          <motion.div
            key="details"
            variants={cardVariants}
            initial="exit"
            animate="enter"
            exit="exit"
            transition={{ type: 'spring', stiffness: 60, damping: 15 }}
            className="bg-zinc-900 p-8 rounded-2xl shadow-lg max-w-3xl mx-auto relative"
          >
            <button
              className="absolute top-4 left-4 text-indigo-400 hover:text-indigo-300 transition"
              onClick={() => (showQR ? setShowQR(false) : setSelected(null))}
            >
              ‚Üê Back
            </button>

            <h3 className="text-2xl font-semibold mb-4 text-indigo-300">{selected.name}</h3>

            <div className="space-y-1 mb-4 text-zinc-200">
              {selected.description.map((d, i) => (
                <p key={i}>{d}</p>
              ))}
              <p className="pt-2 font-semibold text-indigo-400">Duration: {selected.duration}</p>
            </div>

            <div className="flex flex-wrap gap-4 mt-6">
              <button className="bg-zinc-700 text-white px-4 py-2 rounded-lg cursor-default">
                Price: {selected.price}
              </button>
              <motion.button
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => setShowQR(!showQR)}
                className="bg-indigo-500 text-white px-6 py-2 rounded-lg shadow hover:bg-indigo-600 transition"
              >
                {showQR ? 'Close' : 'Pay'}
              </motion.button>
            </div>

            <AnimatePresence>
              {showQR && (
                <motion.div
                  variants={qrVariants}
                  initial="hidden"
                  animate="show"
                  exit="hidden"
                  transition={{ type: 'spring', stiffness: 120 }}
                  className="mt-6 text-center"
                >
                  <p className="text-sm text-zinc-400 mb-2">Scan to pay via Google Pay</p>
                  <img
                    src={selected.qr}
                    alt={`${selected.name} QR`}
                    className="w-40 mx-auto rounded-lg shadow-md"
                  />
                </motion.div>
              )}
            </AnimatePresence>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default AvailableCourses;
