import { useEffect, useRef, useState } from 'react';
import { gsap } from 'gsap';
import '../styles/Header.css';
import { auth, provider } from '../firebase';
import { signInWithPopup, signOut, onAuthStateChanged } from 'firebase/auth';
import logo from '../assets/logo.png';

function Header() {
  const [user, setUser] = useState(null);
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);
  const dropdownRef = useRef(null);

  const nameRef = useRef([]);
  const sloganRef = useRef([]);

  useEffect(() => {
    const unsubscribe = onAuthStateChanged(auth, (currentUser) => {
      setUser(currentUser);
    });

    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowProfileDropdown(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      unsubscribe();
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  useEffect(() => {
    gsap.from(nameRef.current, {
      opacity: 0,
      y: 30,
      stagger: 0.08,
      duration: 0.6,
      ease: 'back.out(1.7)',
    });

    gsap.from(sloganRef.current, {
      opacity: 0,
      y: 20,
      stagger: 0.06,
      duration: 0.5,
      delay: 0.6,
      ease: 'power3.out',
    });
  }, []);

  const handleLogin = async () => {
    try {
      const result = await signInWithPopup(auth, provider);
      setUser(result.user);
      alert(`Welcome, ${result.user.displayName}!`);
    } catch (error) {
      console.error('Sign-in error:', error.message);
      alert(`Sign-in failed: ${error.message}`);
    }
  };

  const handleLogout = async () => {
    try {
      await signOut(auth);
      setUser(null);
    } catch (error) {
      console.error('Logout error:', error.message);
    }
  };

  return (
    <header className="header">
      <div className="company-info">
        <img src={logo} alt="Company Logo" className="company-logo" />
        <div>
          <div className="CompanyName">
            {'ASCORP'.split('').map((char, i) => (
              <span
                key={i}
                ref={(el) => (nameRef.current[i] = el)}
                className="letter"
              >
                {char}
              </span>
            ))}
          </div>
          <div className="CompanySlogan">
            {'Beyond Possibilities'.split('').map((char, i) => (
              <span
                key={i}
                ref={(el) => (sloganRef.current[i] = el)}
                className="letter"
              >
                {char}
              </span>
            ))}
          </div>
        </div>
      </div>

      <nav className="nav-buttons" ref={dropdownRef}>
        {user ? (
          <div className="profile-container">
            <img
              src={user.photoURL}
              alt="Profile"
              className="profile-image"
              onClick={() => setShowProfileDropdown(prev => !prev)}
            />
            {showProfileDropdown && (
              <div className="profile-dropdown">
                <p><strong>{user.displayName}</strong></p>
                <p>{user.email}</p>
                <button className="logout-btn" onClick={handleLogout}>Logout</button>
              </div>
            )}
          </div>
        ) : (
          <button className="nav-button login-button" onClick={handleLogin}>
            Login
          </button>
        )}
      </nav>
    </header>
  );
}

export default Header;
