// src/components/AvailableCourses.js
import  { useState } from 'react';
import '../styles/AvailableCourses.css';
import FinanceQR from '../assets/financeQR.png';
import AiQR from '../assets/AiQR.png';
import MysqlQR from '../assets/MysqlQR.png';
import PromptQR from '../assets/PromptQR.png';
import ResumeQR from '../assets/resumeQR.png';
import InterviewQR from '../assets/interviewQR.png';

// Import course thumbnails directly
import FinanceImage from '../assets/mf.jpg';
import AiImage from '../assets/ai.jpg';
import MysqlImage from '../assets/sql.png';
import PromptImage from '../assets/prompt.jpg';
import ResumeImage from '../assets/resume.jpg';
import InterviewImage from '../assets/interview.jpg';

const courses = [
    {
        id: 1,
        name: 'Finance (Mutual Fund/Stock Market)',
        image: FinanceImage,
        description: [
            'Note: This course is designed for beginners.',
            '1. Learn how to use DigiLocker.',
            '2. Understand how to open a Demat account.',
            '3. Get a basic understanding of the stock market.',
            '4. Familiarize yourself with key financial terms (e.g., P/E ratio, P/B ratio, Profit/Loss statement, Balance Sheet, and more).',
            '5. Learn how to buy and sell shares.',
            '6. Discover how to identify and invest in strong companies.',
            '7. Understand how to avoid weak companies.',
            '8. Learn how to invest in mutual funds.',
            '9. Gain an introduction to fundamental analysis.',
            
            'Outcomes:',
            '1. Protect your family against unexpected financial risks.',
            '2. Strengthen money skills to support family.',
            '3. Find a balance between spending and saving.',
            '4. Break free from financial dependence.',
            '5. Build a secure financial foundation.',
            '6. Build assets for your next generation.'
        ],
        duration: '1 Month (4 live Classes, 2 hours each)',
        price: '499 INR'
    },
    {
        id: 2,
        name: 'Website Development Using AI',
        image: AiImage,
        description: [
            'Note: This Course is for Non-IT Background Students/Professionals (No Coding Knowledge Required)',
            'Outcome:',
            'React based Website Building. (LIVE)',
        ],
        duration: '2 Months (8 live Classes, 2 hours each)',
        price: '799 INR'
    },
    {
        id: 3,
        name: 'Database Management Using Mysql',
        image: MysqlImage,
        description: [
            'Note: This course is designed for Beginners/Students having DBMS.',
            'Outcomes:', 
            '1. How to create and set up databases.',
            '2. How to create tables and define data types for each column.',
            '3. Inserting, updating, and deleting records within tables.',
            '4. Writing basic SQL queries to retrieve specific data.',
            '5. Using filtering and sorting options (e.g., WHERE, ORDER BY).',
            '6. Understanding primary keys and foreign keys to link tables.',
            '7. Performing joins to combine data from multiple tables.',
            '8. Using aggregate functions (e.g., COUNT, SUM, AVG) for data analysis.',
            '9. Implementing basic constraints (e.g., NOT NULL, UNIQUE).',
            '10. Basic data backup and restoration techniques.',
        ],
        duration: '1 Month (4 live Classes, 2 hours each)',
        price: '499 INR'
    },
    {
        id: 4,
        name: 'Prompt Engineering',
        image: PromptImage,
        description: [
            'Outcomes:',
            '1. AI-Powered Image Generation.',
            '2. Text-to-Image and Image-to-Video Creation.', 
            '3. Animated Video Creation.',
            '4. Custom Banners, Thumbnails, and Graphic Design.',
            '5. Logo and Branding Design.', 
            '7. Content Generation for Blogs and Marketing.', 
            '8. Code Generation and Software Development Assistance.',
            '9. Real-Time Language Translation and Summarization.', 
            '10. Grammar and Style Enhancement for Writing.',
            '11. Email Drafting and Reply Suggestions. and so on...',
        ],
        duration: '1 Month (4 live Class, 2 hours each)',
        price: '499 INR'
    },
    {
        id: 5,
        name: 'Resume Building Masterclass',
        image: ResumeImage,
        description: [
            'This will be your final destination to prepare your resume. This is our Guarantee. (Most Recommended for - ITI/Diploma/Degree Students).'
        ],
        duration: '1 Week (1 live Class, about 2-4 hours)',
        price: '199 INR'
    },
    {
        id: 6,
        name: 'Interview Preparation Masterclass',
        image: InterviewImage,
        description: [
            'What you will learn to crack any interview:',
            '1. How to build confidence while speaking in English.',
            '2. How to maintain good body language.',
            '3. How to hide your stress during an interview.',
            '4. How to answer any question.',
            '5. How to maintain a quality conversation.',
            '(Most Recommended for - ITI/Diploma/Degree Students).'
        ],
        duration: '1 Week (1 live Class, about 2-4 hours)',
        price: '199 INR'
    }
];

const AvailableCourses = () => {
    const [selectedCourse, setSelectedCourse] = useState(null);
    const [showQR, setShowQR] = useState(false);

    const handleCourseClick = (course) => {
        setSelectedCourse(course);
        setShowQR(false); // Reset QR visibility when selecting a new course
    };

    const handleBackClick = () => {
        if (showQR) {
            setShowQR(false); // Close QR code view first
        } else {
            setSelectedCourse(null); // Return to course list if QR is already closed
        }
    };

    const handlePaymentClick = () => {
        setShowQR(true);
    };

    const getQRCode = () => {
        switch (selectedCourse?.name) {
            case 'Finance (Mutual Fund/Stock Market)':
                return FinanceQR;
            case 'Website Development Using AI':
                return AiQR;
            case 'Database Management Using Mysql':
                return MysqlQR;
            case 'Prompt Engineering':
                return PromptQR;
            case 'Resume Building Masterclass':
                return ResumeQR;
            case 'Interview Preparation Masterclass':
                return InterviewQR;
            default:
                return null;
        }
    };

    const qrCodeSrc = getQRCode();

    return (
        <div id="available-courses" className="courses-container">
            <h2>Available Courses</h2>
            {!selectedCourse ? (
                <ul className="course-list">
                    {courses.map((course) => (
                        <li
                            key={course.id}
                            className="course-item"
                            onClick={() => handleCourseClick(course)}
                            data-course={course.name.toLowerCase().replace(/\s+/g, '-')}
                        >
                            <img src={course.image} alt={`${course.name} thumbnail`} className="course-thumbnail" />
                            <span>{course.name}</span>
                        </li>
                    ))}
                </ul>
            ) : (
                <>
                    <button className="back-button" onClick={handleBackClick}>
                        ← Back
                    </button>
                    <div className="course-details">
                        <h3>{selectedCourse.name}</h3>
                        <div>
                            {selectedCourse.description.map((desc, index) => (
                                <p key={index}>{desc}</p>
                            ))}
                        </div>
                        <p>Duration: {selectedCourse.duration}</p>
                        <div className="price-and-pay">
                            <button className="price">Price: {selectedCourse.price}</button>
                            <button className="pay-button" onClick={handlePaymentClick}>Pay</button>
                        </div>

                        {showQR && qrCodeSrc && (
                            <div className="qr-code">
                                <p>Scan to pay via Google Pay:</p>
                                <img src={qrCodeSrc} alt={`${selectedCourse.name} Google Pay QR Code`} />
                            </div>
                        )}
                    </div>
                </>
            )}
        </div>
    );
};

export default AvailableCourses;
