// src/components/Navbar.js
import { useState, useEffect, useRef } from 'react';
import '../styles/Navbar.css';    // make sure this file exists
                                  // and defines .navbar, .nav-buttons, etc.

function Navbar() {
  const [showServices, setShowServices] = useState(false);
  const dropdownRef = useRef(null);

  const handleOurServicesClick = () => {
    setShowServices(prev => !prev);
  };

  const handleAvailableCoursesClick = () => {
    const section = document.getElementById('available-courses');
    if (section) section.scrollIntoView({ behavior: 'smooth' });
    setShowServices(false);
  };

  const handleSoftwareSolutionsClick = () => {
    alert("Coming soon");
    setShowServices(false);
  };

  // Close dropdown if clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowServices(false);
      }
    };

    if (showServices) {
      document.addEventListener('mousedown', handleClickOutside);
    } else {
      document.removeEventListener('mousedown', handleClickOutside);
    }

    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, [showServices]);

  return (
    <header className="navbar">
      <nav className="nav-buttons" ref={dropdownRef}>
        <button className="nav-button home-button" onClick={() => window.location.href = '/'}>
          Home
        </button>
        <button className="nav-button">Photo Gallery</button>
        <button className="nav-button">Brochure</button>
        <button className="nav-button" onClick={handleOurServicesClick}>
          Our Services
        </button>

        {showServices && (
          <div className="services-dropdown">
            <button className="dropdown-option" onClick={handleAvailableCoursesClick}>
              Available Courses
            </button>
            <button className="dropdown-option" onClick={handleSoftwareSolutionsClick}>
              Software Solutions
            </button>
          </div>
        )}
      </nav>
    </header>
  );
}

export default Navbar;
