// src/components/Navbar.js
import { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import '../styles/Navbar.css';

// ❶ Import the BoardMembers component
import BoardMembers from './BoardMembers';

function Navbar() {
  const [showServices, setShowServices] = useState(false);
  const [showMenu, setShowMenu] = useState(false);
  const [showBrochure, setShowBrochure] = useState(false);

  // ❷ New state to toggle the BoardMembers view
  const [showBoardMembers, setShowBoardMembers] = useState(false);

  const dropdownRef = useRef(null);

  const handleOurServicesClick = () => {
    setShowServices(prev => !prev);
    setShowMenu(false);
    setShowBrochure(false);
    setShowBoardMembers(false);
  };

  const handleAvailableCoursesClick = () => {
    document
      .getElementById('available-courses')
      ?.scrollIntoView({ behavior: 'smooth' });
    setShowServices(false);
  };

  const handleSoftwareSolutionsClick = () => {
    alert("Coming soon");
    setShowServices(false);
  };

  const handleMenuClick = () => {
    setShowMenu(prev => !prev);
    setShowServices(false);
    setShowBrochure(false);
    setShowBoardMembers(false);
  };

  const handleBrochureClick = () => {
    setShowBrochure(prev => !prev);
    setShowMenu(false);
    setShowServices(false);
    setShowBoardMembers(false);
  };

  // ❸ Toggle BoardMembers
  const handleBoardMembersClick = () => {
    setShowBoardMembers(prev => !prev);
    setShowMenu(false);
    setShowServices(false);
    setShowBrochure(false);
  };

  // Close all dropdowns (and BoardMembers) if clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowServices(false);
        setShowMenu(false);
        setShowBrochure(false);
        setShowBoardMembers(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <>
      <header className="navbar">
        <nav className="nav-buttons" ref={dropdownRef}>
          {/* Menu Dropdown */}
          <button className="nav-button" onClick={handleMenuClick}>
            Menu
          </button>

          {showMenu && (
            <div className="menu-dropdown">
              <button
                className="dropdown-option"
                onClick={() => (window.location.href = '/')}
              >
                Home
              </button>
              <Link
                to="https://comeonsom.github.io/Big-Pocket.com/Other-files/about.html"
                className="dropdown-option"
              >
                About Company
              </Link>

              {/*  Board Members toggle */}
              <button
                className="dropdown-option"
                onClick={handleBoardMembersClick}
              >
                Board Members
              </button>

              <button
                className="dropdown-option"
                onClick={handleBrochureClick}
              >
                Course Brochure
              </button>
            </div>
          )}

          {/* Brochure Dropdown (commented out) */}
          {/*
          {showBrochure && (
            <div className="menu-dropdown">
              <a href={brochure1} className="dropdown-option" target="_blank" rel="noopener noreferrer">
                Brochure 1
              </a>
              <a href={brochure2} className="dropdown-option" target="_blank" rel="noopener noreferrer">
                Brochure 2
              </a>
              <a href={brochure3} className="dropdown-option" target="_blank" rel="noopener noreferrer">
                Brochure 3
              </a>
            </div>
          )}
          */}

          {/* Services Dropdown */}
          <button className="nav-button" onClick={handleOurServicesClick}>
            Our Services
          </button>
          {showServices && (
            <div className="services-dropdown">
              <button
                className="dropdown-option"
                onClick={handleAvailableCoursesClick}
              >
                Available Courses
              </button>
              <button
                className="dropdown-option"
                onClick={handleSoftwareSolutionsClick}
              >
                Software Solutions
              </button>
            </div>
          )}

          {/* Visit Big Pocket */}
          <a
            href="https://comeonsom.github.io/Big-Pocket.com/"
            target="_blank"
            rel="noopener noreferrer"
            className="nav-button"
          >
            Visit Big Pocket
          </a>
        </nav>
      </header>

      {/* ❺ Conditionally render BoardMembers inline below the header */}
      {showBoardMembers && <BoardMembers />}
    </>
  );
}

export default Navbar;
