// src/components/Navbar.js
import { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom'; // Make sure react-router-dom is installed
import '../styles/Navbar.css';

function Navbar() {
  const [showServices, setShowServices] = useState(false);
  const [showMenu, setShowMenu] = useState(false);
  const dropdownRef = useRef(null);

  const handleOurServicesClick = () => {
    setShowServices(prev => !prev);
    setShowMenu(false); // Close menu if open
  };

  const handleAvailableCoursesClick = () => {
    const section = document.getElementById('available-courses');
    if (section) section.scrollIntoView({ behavior: 'smooth' });
    setShowServices(false);
  };

  const handleSoftwareSolutionsClick = () => {
    alert("Coming soon");
    setShowServices(false);
  };

  const handleMenuClick = () => {
    setShowMenu(prev => !prev);
    setShowServices(false); // Close services if open
  };

  // Close dropdowns if clicking outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowServices(false);
        setShowMenu(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  return (
    <header className="navbar">
      <nav className="nav-buttons" ref={dropdownRef}>

        {/* Menu button */}
        <button className="nav-button" onClick={handleMenuClick}>
          Menu
        </button>

        {/* Dropdown Menu */}
        {showMenu && (
          <div className="menu-dropdown">
            <button className="dropdown-option" onClick={() => window.location.href = '/'}>
              Home
            </button>
            <Link to="/about" className="dropdown-option">
              About
            </Link>
            <button className="dropdown-option">
              Brochure
            </button>
          </div>
        )}

        {/* Our Services dropdown */}
        <button className="nav-button" onClick={handleOurServicesClick}>
          Our Services
        </button>

        {showServices && (
          <div className="services-dropdown">
            <button className="dropdown-option" onClick={handleAvailableCoursesClick}>
              Available Courses
            </button>
            <button className="dropdown-option" onClick={handleSoftwareSolutionsClick}>
              Software Solutions
            </button>
          </div>
        )}

        {/* Visit Big Pocket */}
        <a href="https://bigpocket.in" target="_blank" rel="noopener noreferrer" className="nav-button">
          Visit Big Pocket
        </a>
      </nav>
    </header>
  );
}

export default Navbar;
