import { useState } from 'react';
import '../styles/AvailableCourses.css';
import FinanceQR from '../assets/financeQR.png';
import PromptQR from '../assets/PromptQR.png';
import FinanceImage from '../assets/mf.jpg';
import PromptImage from '../assets/prompt.jpg';

const courses = [
    {
        id: 1,
        name: 'Finance',
        image: FinanceImage,
        qr: FinanceQR,
        description: [
            'Note: What We Will Cover! (From Scratch)',
            '1. Stock Market',
            '2. Mutual Fund',
            '3. Investment in Gold',
            '4. Lending Money',
            '5. Smart FD',
            '6. Portfolio Management',
            '7. AI Systems in Finance',
        ],
        duration: '4 Month (16 live Classes, 2 hours each)',
        price: '1999 INR',
    },
    {
        id: 2,
        name: 'Prompt Engineering',
        image: PromptImage,
        qr: PromptQR,
        description: [
            'What We Will Cover!:',
            '1. AI-Powered Image Generation.',
            '2. Text-to-Image and Image-to-Video Creation.',
            '3. Custom Banners, Thumbnails, and Graphic Design.',
            '4. Logo and Branding Design.', 
            '5. Content Generation for Blogs and Marketing.',
            '6. Real-Time Language Translation and Summarization.', 
            '7. Grammar and Style Enhancement for Writing.',
            '8. Email Drafting and Reply Suggestions.',
            '9. Website Development Using AI',
            '10. Resume Building Using AI.'
        ],
        duration: '2 Month (8 live Class, 2 hours each)',
        price: '1199 INR',
    },
];

const AvailableCourses = () => {
    const [expandedId, setExpandedId] = useState(null);
    const [qrCourse, setQrCourse] = useState(null);

    const toggleExpand = (id) => {
        setExpandedId(prev => (prev === id ? null : id));
    };

    const openQR = (course) => {
        setQrCourse(course);
    };

    const closeQR = () => {
        setQrCourse(null);
    };

    return (
        <div id="available-courses" className="courses-container">
            <h2>Available Courses</h2>
            <ul className="course-list">
                {courses.map(course => (
                    <li key={course.id} className="course-card">
                        <div className="course-header" onClick={() => toggleExpand(course.id)}>
                            <img src={course.image} alt={course.name} className="course-thumbnail" />
                            <div>
                                <h3>{course.name}</h3>
                                <p>{expandedId === course.id ? 'Click to collapse' : 'Click to view details'}</p>
                            </div>
                        </div>

                        {expandedId === course.id && (
                            <div className="course-details">
                                {course.description.map((line, i) => (
                                    <p key={i}>{line}</p>
                                ))}
                                <p><strong>Duration:</strong> {course.duration}</p>
                                <p><strong>Price:</strong> {course.price}</p>
                                <button className="pay-button" onClick={() => openQR(course)}>Pay Now</button>
                            </div>
                        )}
                    </li>
                ))}
            </ul>

            {qrCourse && (
                <div className="qr-modal">
                    <div className="qr-content">
                        <button className="close-button" onClick={closeQR}>×</button>
                        <h3>{qrCourse.name} – Scan to Pay</h3>
                        <img src={qrCourse.qr} alt="QR Code" className="qr-code" />
                    </div>
                </div>
            )}
        </div>
    );
};

export default AvailableCourses;
